		<script type="text/javascript">
			$(function(){
				var requiredCheckboxes = $('.options :checkbox[required]');
				requiredCheckboxes.change(function(){
					if(requiredCheckboxes.is(':checked')) {
						requiredCheckboxes.removeAttr('required');
					} else {
					requiredCheckboxes.attr('required', 'required');
					}
				});
			});

			$(document).ready(function(){
				var x = new Date();
				var year = x.getFullYear();
				var month = x.getMonth()+1; //month is zero-indexed for whatever reason
				if(month<10){
					month = "0" + month;
				}
				var date = x.getDate();
				if(date<10){
					date = "0" + date;
				}
				var hours = x.getHours();
				if(hours<10){
					hours = "0" + hours;
				}
				var minutes = x.getMinutes();
				if(minutes<10){
					minutes = "0" + minutes;
				}
				var date_time = year + "-" + month + "-" + date + "T" + hours + ":" + minutes + ":00";
				document.getElementById('expires').value = date_time;
				document.getElementById('expires').stepUp(1440); // add 24 hrs for default expiration time
			});
		</script>

		<script type="text/javascript">

			function validateForm(){
				console.log("validate form is running");
				var geocheck = document.getElementById('geocheck').checked;
				var codecheck = document.getElementById('codecheck').checked;
				var fipscheck = document.getElementById('fipscheck').checked;

				var codeCount = document.getElementById('geocodeDiv').getElementsByClassName("search-choice").length
				var fipsCount = document.getElementById('stateDiv').getElementsByClassName("search-choice").length
				var geoCount = 0;

				for(var i=0; i<polysOnMap.length; i++){
					if(!(polysOnMap[i] === null)){
						geoCount++;
					}
				}

				if(!geocheck && !codecheck && !fipscheck){
					alert("You MUST choose at least one location option!");
					return false;
				}else if(codecheck && codeCount == 0){
					alert("If including a SAME code, you must select at least one county");
					return false;
				}else if(fipscheck && fipsCount == 0){
					alert("If including a FIPS code, you must select at least one state");
					return false;
				}else if(geocheck && geoCount == 0){
					alert("If including a polygon, you must draw at least one polygon");
					return false;
				}else{
					enableBlur();
				}
			}

			function geoCheck(){
				var poly = document.getElementById("polygon");
				if(document.getElementById('geocheck').checked){
					document.getElementById("map").style="";
					poly.disabled = false;
					poly.required = true;
				}else{
					poly.disabled = true;
					poly.required = false;
					if(!document.getElementById('codecheck').checked && !document.getElementById('fipscheck').checked){
						alert("Reminder: You MUST choose at least one location option!");
					}
					document.getElementById("map").style="visibility:hidden;display:none;";
				}
			}

			function codeCheck(){
				var gc = document.getElementById('geocodeDiv'); // this is the div
				var gcs = document.getElementById('geocode'); // this is the select

				if(document.getElementById('codecheck').checked){
					gc.style = "visibility:visible";
//					gc.style = "overflow-y:scroll;width:100%;margin-bottom:10px;background-color:#154144;color:white;";
					gcs.disabled = false;
					gcs.required = true;
				}else{
					gcs.disabled = true;
					gcs.required = false;
					if(!document.getElementById('geocheck').checked && !document.getElementById('fipscheck').checked){
						alert("Reminder: You MUST choose at least one location option!");
					}
					gc.style = "visibility:hidden;";
				}
			}

			function fipsCheck(){
				var fc = document.getElementById('stateDiv');
				var fcs = document.getElementById('fipscode');
				if(document.getElementById('fipscheck').checked){
					fc.style = "visibility:visible";
					fcs.disabled = false;
					fcs.required = true;
				}else{
					$('#fipscode option').prop("selected",false);
					$('#fipscode').trigger("chosen:updated");
					$('#fipscode').trigger("change");
//					$("fipscode option:selected").prop("selected",false);
					fcs.disabled = true;
					fcs.required = false;
					if(!document.getElementById('geocheck').checked && !document.getElementById('codecheck').checked){
						alert("Remider: You MUST choose at least one location option!");
					}
					fc.style = "visibility:hidden;";
					//removeAllStatesFromMap();
				}
			}
